// SPDX-License-Identifier: MIT

pragma solidity ^0.6.0;

import "@openzeppelin/contracts/token/ERC20/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "./MinterLimit.sol";

contract KnitStellar is ERC20, ERC20Burnable, Ownable, MinterLimit {

  constructor() public ERC20("Knit Stellar", "kXLM") {}

  function mint(address to, uint256 amount) public {
    require(minters[msg.sender], "No permission for minting");
    mintersLimit[msg.sender] = mintersLimit[msg.sender].sub(amount, "Minting limit exceeded");
    minted[msg.sender] = minted[msg.sender].add(amount);
    _mint(to, amount);
  }
}
