// SPDX-License-Identifier: MIT

pragma solidity ^0.6.0;

import "@openzeppelin/contracts/access/Ownable.sol";

contract MinterLimit is Ownable {

  mapping(address => bool) internal minters;
  mapping(address => uint256) internal mintersLimit;
  mapping(address => uint256) internal minted;
  event AddMinter(address _address);
  event AddMinterLimit(address _address, uint256 amount);
  event RemoveMinterLimit(address _address, uint256 amount);

  function addMinter(address _address) public onlyOwner {
    minters[_address] = true;
    emit AddMinter(_address);
  }

  function getMinter(address _address) public view returns (bool) {
    return minters[_address];
  }

  function addMinterLimit(address _address, uint256 amount) public onlyOwner {
    mintersLimit[_address] += amount;
    emit AddMinterLimit(_address, amount);
  }

  function removeMinterLimit(address _address, uint256 amount) public onlyOwner {
    uint256 minterBalance = mintersLimit[_address];
    require(minterBalance >= amount, "Exceed removal limit");
    mintersLimit[_address] = minterBalance - amount;
    emit RemoveMinterLimit(_address, amount);
  }

  function getLimit(address _address) public view returns (uint256) {
    return mintersLimit[_address];
  }

  function getMinted(address _address) public view returns (uint256) {
    return minted[_address];
  }
}
